import React, {Fragment} from 'react';
import Header from './Header'
import {Global, css } from '@emotion/core';


const Layout = props => {
    return ( 
        <Fragment>
            {/*Se aplican a todos los estilos globales*/}
            <Global 
                styles={css`
                    :root{
                        --gris: #3d3d3d;
                        --gris2: #6F6F6F;
                        --naranja: #DA552F;
                    }
                    html{
                        font-size: 62.5%;
                        box-sizing: border-box;
                    }
                    *, *:before, *:after {
                        box-sizing: inherint;
                    }
                    body{
                        font-size: 1.6rem;
                    }
                    h1,h2,h3{
                        margin: 0 0 2rem;
                        line-height: 1.5;
                    }
                    ul{
                        list-style: none;
                        margin: 0;
                        padding: 0;
                    }
                    a{
                        text-decoration: none;
                    }
                `}
            />
            <Header>

            </Header>

            <main>
                {props.children}
            </main>
        </Fragment>
    );
}

export default Layout;
