import React, { Fragment } from 'react';
import { css } from '@emotion/core';
import Layout from '../components/layout/Layout';
import { Formulario, Campo, InputSubmit, Errores } from '../components/UI/Formulario';

import useValidacion from '../hooks/useValidacion';
import validarCrearCuenta from '../validacion/validarCrearCuenta';


const CrearCuenta = () => {

  const STATE_INICIAL = {
    nombre: '',
    email: '',
    password: ''
  }
  const {valores, errores, submitForm, handleChange, handleSubmit} = useValidacion(STATE_INICIAL, validarCrearCuenta, crearCuenta);

  const{nombre, email, password} = valores;

  function crearCuenta(){
    console.log("Creando cuenta");
  }

  console.log(errores);

  return (
    <div>
      <Layout>
        <Fragment>
          <h1
            css={css`
              text-align: center;
              margin-top: 5rem;
            `}
          >Crear Cuenta</h1>
          <Formulario
            onSubmit={handleSubmit}
          >
            <Campo>
              <label htmlFor="nombre">Nombre</label>
              <input
                type="text"
                id="nombre"
                placeholder="Tu nombre"
                name="nombre"
                value={nombre}
                onChange={handleChange}
              />
            </Campo>

            {errores.nombre && <Errores>{errores.nombre}</Errores>}

            <Campo>
              <label htmlFor="email">Email</label>
              <input
                type="text"
                id="email"
                placeholder="Tu email"
                name="email"
                value={email}
                onChange={handleChange}
              />
            </Campo>

            <Campo>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                placeholder="Tu password"
                name="password"
                value={password}
                onChange={handleChange}
              />
            </Campo>

            <InputSubmit
              type="submit"
              value="Crear Cuenta"

            />

          </Formulario>
        </Fragment>
      </Layout>

    </div>
  )
}

export default CrearCuenta;
