import React, {Fragment} from 'react';
import Header from './Header'
import {Global, css } from '@emotion/core';


const Layout = props => {
    return ( 
        <Fragment>
            {/*Se aplican a todos los estilos globales*/}
            <Global 
                styles={css`
                    :root{
                        --gris: #3d3d3d;
                        --gris2: #6F6F6F;

                    }
                `}
            />
            <Header>

            </Header>

            <main>
                {props.children}
            </main>
        </Fragment>
    );
}

export default Layout;
